define(["controller/controllers"],function(controllers){controllers.controller("portlet_list",["$scope","$rootScope","$location",function($scope,$rootScope,$location){$scope.PAGE_NO=0;$scope.PAGE_SIZE=5;$scope.click_showList=function(){$location.url("/"+$scope.url+"/list")};$scope.click_showView=function(key){$location.url("/"+$scope.url+"/view/"+key)};$scope.getPortlet=function(api){$scope.isLoading=true;$scope.$parent.getList(api,"list",{NO:$scope.PAGE_NO,SIZE:$scope.PAGE_SIZE},{SYSTEM_GB:"CMS",NOTICE_FL:"Y"},true).then(function(data){$scope.list=data})["catch"](function(error){$scope.list=[];console.log(error)})["finally"](function(){$scope.isLoading=false})}}])});